import React, {useEffect,useState,useRef} from 'react';
import '../css/test.css'
import Start from './Start.js'
import testData from "../Data/data.json"
import Checkbox  from "@material-ui/core/Checkbox"
import YourAnswers from "./YourAnswers";

const Test = () => {

    const[step , setStep] = useState(1);
    const[answer , setAnswer] = useState("");
    const[numberOfQuestion , setnumberOfQuestion] = useState(0);
    const[boolY , setBoolY] = useState(false);
    const[boolN , setBoolN] = useState(false);
    const[boolS , setBoolS] = useState(false);
    const[error , setError] = useState("");
    const[status , setStatus] = useState("");




    const answerDAta = [

        {
           answer:""

        },
        {
            answer:""

        },
        {
            answer:""

        },
        {
            answer:""

        },
        {
            answer:""

        },
        {
            answer:""

        },
        {
            answer:""

        },
        {
            answer:""

        }
    ]



    const  mainFunction = () => {


        setBoolY(false)
        setBoolN(false)
        setBoolS(false)

        if(boolS === false && boolN === false && boolY === false){

            setError("Any form can't be empty!")

        }else{

         answerDAta[numberOfQuestion].answer = answer

      testData.data[numberOfQuestion].answer = answer

            {answerDAta[7].answer && setnumberOfQuestion(0)}

            if (answerDAta[7].answer !== ""){

                console.log(answerDAta[7].answer)
            }

         if(numberOfQuestion < testData.data.length-1) {

            setnumberOfQuestion(numberOfQuestion + 1)


          }else{

            setStep(3);


         }

        }


    }



const checkedFunc = (e) =>{
    setError("")

    if(e === "yes")
{
    setBoolY(true)
    setBoolN(false)
    setBoolS(false)

}

    if(e === "no")
    {
        setBoolY(false)
        setBoolN(true)
        setBoolS(false)
    }

    if(e === "sometimes")
    {
        setBoolY(false)
        setBoolN(false)
        setBoolS(true)
    }
}


    return(

        <div>


        <div className="test_header">

            <h2 className='test_header_text'>CoronaVirus Test</h2>

        </div>

     <div className="test_questions">


         {step === 1 &&
         <Start/>
         }


         {step === 2 &&
         <div className='question_div'>

             <h2 className='question_text'>{testData.data[numberOfQuestion].question}</h2>


             {error !== "" &&

             <div  className="alert alert-danger error_div">
                 <strong style={{color:"#721c24"}}>Error!:</strong> {error}
             </div>

         }

             {error === "" &&

             <div  className= {numberOfQuestion === 2 ? "alert alert-primary description_div2" : "alert alert-primary description_div"}>
                 <strong style={{color:"#92272f"}}>{testData.data[numberOfQuestion].description}</strong>
             </div>

             }



         </div>

         }








     </div>

            <div className="test_informations">


                {step === 3 &&
                <YourAnswers/>
                }


                {step === 1 &&

                    <div>

                        <img width='780px' src="./images/step1.jpg"/>

                    </div>
                }

                {step === 2 &&

                <div className='whenTest'>

                    <br/>

                    <h3>YOUR ANSWERED</h3>

                    <h1>{numberOfQuestion}/8</h1>




                    <br/>
                    <h3>QUESTIONS</h3>

                </div>
                }


            </div>



            <div className="test_inputs">

                {step === 1 &&
                <a className='start_button' onClick={ (e) => setStep(2) }>Start</a>
                }


                {step === 2 &&

                <div  className='checkbox_div'>

                    <Checkbox name = "check" color='primary' value='yes' onChange={(e)=>setAnswer(e.target.value)} onClick={(e) => checkedFunc(e.target.value) } checked={boolY}/>
                    <label htmlFor="yes">Yes</label>
                    <Checkbox name = "check" color='primary' value='no' onChange={(e)=>setAnswer(e.target.value)} onClick={(e) => checkedFunc(e.target.value) } checked={boolN}/>
                    <label htmlFor="no">No</label>
                    <Checkbox name = "check" color='primary' value='sometimes'  onChange={(e)=>setAnswer(e.target.value)}  onClick={(e) => checkedFunc(e.target.value) } checked={boolS}/>
                    <label htmlFor="sometimes">Sometimes</label>
                    <button className='btn btn-primary' type='button' onClick={mainFunction}>Next</button>





                </div>
                }

                {step === 3 &&

                    <a className='start_button'  onClick={ (e) => setStep(2)}>Try It</a>
                }



                    </div>


        </div>

    );

}

export default Test;
