import React from 'react';
import "../css/test.css"
const Checkbox = () =>{

    function selectOnlyThis(id){
        const myCheckbox = document.getElementsByName("myCheckbox");
        Array.prototype.forEach.call(myCheckbox,function(el){
            el.checked = false;
        });
        id.checked = true;
    }

    return(

        <div  className='checkbox_div'>

            <input type="checkbox" value='1' id='yes' name='myCheckbox' onclick={selectOnlyThis(this)}/>
            <label htmlFor="yes">Yes</label>
            <input type="checkbox" value='2' id='no' name='myCheckbox' onclick={selectOnlyThis(this)}/>
            <label htmlFor="no">No</label>
            <input type="checkbox" value='3' id='sometimes'name='myCheckbox' onclick={selectOnlyThis(this)}/>
            <label htmlFor="sometimes">Sometimes</label>
            <button className='btn btn-success' type='button'>Next</button>
        </div>


    );

}

export default Checkbox;
