import React, {useEffect , useState} from 'react';
import '../css/met.css';
import numeral from "numeral";

function Main() {

    const[worldInfo , setWorldInfo] = useState({});
    const[countries , setCountries] = useState([]);
    const[continents , setContinents] = useState([]);
    const[continentsT , setContinentsT] = useState(false);




    useEffect(() => {

        fetch('https://disease.sh/v3/covid-19/all')
            .then((response) => response.json())
            .then((data) => {

               setWorldInfo(data);

            });

    },[]);




    useEffect( () => {

        const getCountries = async () => {

        fetch('https://disease.sh/v3/covid-19/countries')
            .then((response) => response.json())
            .then((data) => {

                const countries  = data.map((country) => ({

                    country : country.country,
                    cases : country.cases,
                    deaths : country.deaths,
                    recovered : country.recovered,
                    active : country.active,
                    critical: country.critical,

                }));
                setCountries(countries);
            });

        }

        getCountries();

    },[]);








    const getContinent = async (ContinentName) => {
        setContinentsT(true)

        fetch(`https://disease.sh/v3/covid-19/continents/${ContinentName}`)
            .then((response) => response.json())
            .then((data) => {

                const countries  = data.countries.map((country) => ({

                    country : country,

                }));


           setContinents(countries)
            });

    }


    return (
        <div>


            <body>


            <div className="gov">
                <h1 className="ct">CoronaVirus Test</h1>

                <p className="ct2">1.7 million people died in the world due to the Corona Virus</p>
                <p className="ct3"> CoronaVirus is a dangerous threat to the elderly, children and families</p>
                <p className="ct4">Please do the CoronaVirus test to protect our world!</p>
                <a href="rcb3.jpg" className="btnA">CoronaVirus Test</a>


            </div>

            <div className="coL">


                <div className="box2">
                    <ul className="ul3">

                        <li className="list-inline-item">Countries </li>
                        <li className="list-inline-item li2">Recovered</li>
                        <li className="list-inline-item li2">Deaths</li>
                        <li className="list-inline-item li2">Cases</li>
                        <li className="list-inline-item li2 ">Active</li>
                        <li className="list-inline-item li2">Critical</li>

                    </ul>


                    {continentsT === false &&

                    <ul className="ul2">




                        {

                            countries.map((country , i) => (


                                <li className="list-inline-item cou"><span>{++i}</span>{country.country}


                                    <ul>


                                        <li className="list-inline-item li3 li31">{numeral(country.recovered).format('0,0')}</li>
                                        <li className="list-inline-item li3 li32">{numeral(country.deaths).format('0,0')}</li>
                                        <li className="list-inline-item li3 li33">{numeral(country.cases).format('0,0')}</li>
                                        <li className="list-inline-item li3 li34">{numeral(country.active).format('0,0')}</li>
                                        <li className="list-inline-item li3 li35">{numeral(country.critical).format('0,0')}</li>

                                    </ul>


                                </li>


                            ))

                        }



                    </ul>
                    }







                    {continentsT === true &&

                    <ul className="ul2">




                        {

                            continents.map((continent ) => (

                                countries.map((country , i) => (





                                    <li className="list-inline-item cou"><span>{++i}</span>{continent.country}


                                        <ul>


                                            <li className="list-inline-item li3 li31">{numeral(country.recovered).format('0,0')}</li>
                                            <li className="list-inline-item li3 li32">{numeral(country.deaths).format('0,0')}</li>
                                            <li className="list-inline-item li3 li33">{numeral(country.cases).format('0,0')}</li>
                                            <li className="list-inline-item li3 li34">{numeral(country.active).format('0,0')}</li>
                                            <li className="list-inline-item li3 li35">{numeral(country.critical).format('0,0')}</li>

                                        </ul>


                                    </li>



                                ))


                            ))

                        }







                    </ul>
                    }


                </div>


                <div className="box">
                    <h2>Countries</h2>
                    <ul>

                        <li onClick={(e) => {getContinent("Africa")}}><span>1</span>Afrika</li>
                        <li onClick={(e) => {getContinent("Asia")}}><span>2</span>Asia</li>
                        <li onClick={(e) => {getContinent("Europe")}}><span>3</span>Europe</li>
                        <li onClick={(e) => {getContinent("North%20America")}}><span>4</span>North America</li>
                        <li onClick={(e) => {getContinent("South%20America")}}><span>5</span>South America</li>
                        <li onClick={(e) => {getContinent("Australia")}}><span>6</span>Australia</li>


                    </ul>


                </div>

