import React, {useEffect,useState,useRef} from 'react';
import '../css/test.css'
import Start from './Start.js'
import Question from './Question.js'
import testData from "../Data/data.json"
import Checkbox  from "@material-ui/core/Checkbox"



const Test = () => {

    const[step , setStep] = useState(1);
    const[answer , setAnswer] = useState("");
    const[numberOfQuestion , setnumberOfQuestion] = useState(0);

    const answerDAta = [

        {
           answer:""

        },
        {
            answer:""

        },
        {
            answer:""

        }
    ]

    var checkedT = "";

    const yesCheckedFunction = () =>{



    }


    const  mainFunction = () => {

        answerDAta[numberOfQuestion].answer = answer

        console.log(answerDAta[numberOfQuestion].answer)

         if(numberOfQuestion < testData.data.length-1) {

            setnumberOfQuestion(numberOfQuestion + 1)

          }else{

            setStep(3);

         }


        function onlyOne(checkbox) {
            var checkboxes = document.getElementsByName('check')
            checkboxes.forEach((item) => {
                if (item !== checkbox) item.checked = false
            })
        }

    }
    return(

        <div>


        <div className="test_header">

            <h2 className='test_header_text'>CoronaVirus Test</h2>

        </div>

     <div className="test_questions">


         {step === 1 &&
         <Start/>
         }


         {step === 2 &&
         <div className='question_div'>

             <h2 className='question_text'>{testData.data[numberOfQuestion].question}</h2>


         </div>

         }


     </div>

            <div className="test_informations">


            </div>



            <div className="test_inputs">

                {step === 1 &&
                <a className='start_button' onClick={ (e) => setStep(2) }>Start</a>
                }


                {step === 2 &&

                <div  className='checkbox_div'>

                    <Checkbox color='primary' value='yes' onChange={(e)=>setAnswer(e.target.value)} checked="" />
                    <label htmlFor="yes">Yes</label>
                    <Checkbox color='primary' value='no' onChange={(e)=>setAnswer(e.target.value)}/>
                    <label htmlFor="no">No</label>
                    <Checkbox color='primary' value='sometimes'  onChange={(e)=>setAnswer(e.target.value)}/>
                    <label htmlFor="sometimes">Sometimes</label>
                    <button className='btn btn-primary' type='button' onClick={mainFunction}>Next</button>


                    <div style={{display:"none"}}>

                        <Question data={testData.data[numberOfQuestion].question} />


                    </div>


                </div>
                }

                {step === 3 &&

                    <a className='start_button' onClick={ (e) => setStep(2) }>Try It</a>
                }



                    </div>


        </div>

    );

}

export default Test;
