import React, {Component , useState,useEffect} from 'react';
import PropTypes from 'prop-types';
import './css/header.css';
import {FormControl,MenuItem,Select} from "@material-ui/core"
import InfoStats from "./infoStats";
import './css/infoStats.css'


function Header(){

    const[countries,setCountries] = useState([]);
    const[country,setCountry] = useState("WorldWide");
    const[countryInfo,setCountryInfo] = useState({});



    useEffect(() => {

        fetch('https://disease.sh/v3/covid-19/all')
            .then((response) => response.json())
            .then((data) => {

                setCountryInfo(data);

            });

    },[]);


    useEffect(() => {


        const getCountries = async () => {

            await fetch('https://disease.sh/v3/covid-19/countries')
                .then((response) => response.json())
                .then((data) => {

                    const countries = data.map((country) => ({

                        name : country.country,
                        value : country.countryInfo.iso2,

                    }));

                    setCountries(countries);


                })


        }


        getCountries();

    },[])






    const onCountryChange = async (event) =>{

        const countryCode = event.target.value;

        const url = `https://disease.sh/v3/covid-19/countries/${countryCode}`;

        await fetch(url)
            .then((response) => response.json())
            .then((data) => {

                setCountry(countryCode);
                setCountryInfo(data);

            });


    };




    return (
            <div>

                <div className="map_header">

                    <h1 className='header_text'>CoronaVirus Map</h1>

                    <FormControl className='map_dropdown'>

                        <Select value={country} onChange={onCountryChange} variant='outlined'>

                            <MenuItem  value="WorldWide">WorldWide</MenuItem>


                            {
                                countries.map((country) => (

                                 <MenuItem value={country.value}>{country.name}</MenuItem>

                                ))

                            }




                        </Select>


                    </FormControl>


                </div>



                <div className="map_stats">


                    <InfoStats title = "Cases" total = {countryInfo.cases} count = {countryInfo.todayCases}/>

                    <InfoStats title = "Recoreved" total = {countryInfo.recovered} count = {countryInfo.todayRecovered}/>

                    <InfoStats title = "Deaths" total = {countryInfo.deaths} count = {countryInfo.todayDeaths}/>
                </div>



            </div>




        );

}



export default Header;
