import React, {Component , useState,useEffect} from 'react';
import PropTypes from 'prop-types';
import './css/header.css';
import {FormControl,MenuItem,Select} from "@material-ui/core"
import InfoStats from "./infoStats";
import './css/infoStats.css'
import LineGraph from './LineGraph';
import './css/lineGraph.css';
import Map from "./Map";
import "leaflet/dist/leaflet.css";
import {prettyPrintStat} from "./operationsUtils";



function Header(){

    const[countries,setCountries] = useState([]);
    const[country,setCountry] = useState("WorldWide");
    const[countryInfo,setCountryInfo] = useState({});
    const[mapCenter, setMapCenter] = useState({lat: 41.42067843862231, lng: 46.93269214775548} );
    const [mapZoom, setMapZoom] = useState(3);
    const[mapCountry , setMapCountry] = useState([]);






    useEffect(() => {

        fetch('https://disease.sh/v3/covid-19/all')
            .then((response) => response.json())
            .then((data) => {

                setCountryInfo(data);

            });

    },[]);


    useEffect(() => {


        const getCountries = async () => {

            await fetch('https://disease.sh/v3/covid-19/countries')
                .then((response) => response.json())
                .then((data) => {

                    const countries = data.map((country) => ({

                        name : country.country,
                        value : country.countryInfo.iso2,

                    }));

                    setCountries(countries);
                    setMapCountry(data);
                })


        }


        getCountries();

    },[])







    const onCountryChange = async (event) =>{

        const countryCode = event.target.value;

        const url =
            countryCode === 'WorldWide'
                ? "https://disease.sh/v3/covid-19/all"
                : `https://disease.sh/v3/covid-19/countries/${countryCode}`;

        await fetch(url)
            .then((response) => response.json())
            .then((data) => {

                setCountry(countryCode);
                setCountryInfo(data);


                setMapCenter([data.countryInfo.lat , data.countryInfo.long]);
                setMapZoom(4);

            });



    };



    return (
            <div>

                <div className="map_header">



                    <h1 className='header_text'>CoronaVirus Map</h1>

                    <FormControl className='map_dropdown'>

                        <Select value={country} onChange={onCountryChange} variant='outlined'>

                            <MenuItem  value="WorldWide">WorldWide</MenuItem>


                            {
                                countries.map((country) => (

                                 <MenuItem value={country.value}>{country.name}</MenuItem>

                                ))

                            }




                        </Select>


                    </FormControl>


                </div>



                <div className="map_stats">


                    <InfoStats
                        title = "Cases" total = {prettyPrintStat(countryInfo.cases)} count = {prettyPrintStat(countryInfo.todayCases)}/>

                    <InfoStats
                        title = "Recovered" total = {prettyPrintStat(countryInfo.recovered)} count = {prettyPrintStat(countryInfo.todayRecovered)}/>
                    <InfoStats
                        title = "Deaths" total = {prettyPrintStat(countryInfo.deaths)} count = {prettyPrintStat(countryInfo.todayDeaths)}/>                </div>



                <div className="stats_graph">

                    <LineGraph />

                </div>


                <div className="map">


                    <Map countries = {mapCountry} center={mapCenter} zoom={mapZoom}/>

                </div>

            </div>



        );






}



export default Header;
