import React, { Component , useState , useEffect} from 'react';
// import '../css/forLogin.css'
import Axios from "axios";


const H2 = () => {

    Axios.defaults.withCredentials = true;


    const[userMailLog ,  setUserMailLog] = useState();
    const[userPasswordLog ,  setUserPasswordLog] = useState();


    const loginFuncs = () => {


        Axios.post('http://localhost:4000/login' , {mail:userMailLog , password:userPasswordLog})
            .then((response) => {

            if (response.data.message){

                console.log(response.data.message)
            }else{

                console.log(response.data[0].user_name)


            }
        })

    }



    return(
        <div>

            <form action="http://localhost:3000/">

            <input type="text" onChange = {(e) => setUserMailLog(e.target.value)}/>
            <input type="password" onChange = {(e) => setUserPasswordLog(e.target.value)}/>

            <button onClick={loginFuncs} >Submit</button>

            </form>
        </div>
    );

}

export default H2;
