import React from 'react';
import {Circle , Popup} from "react-leaflet";
import numeral from "numeral";
import './css/mapLeafte.css'


const casesTypeColors = {
    cases: {
        hex: "#CC1034",
        multiplier: 200,
    },
    recovered: {
        hex: "#7dd71d",
        multiplier: 300,
    },
    deaths: {
        hex: "#fb4443",
        multiplier: 400,
    },
};




    export const showDataOnMap = (data, casesType = "cases") =>
        data.map((country) => (
            <Circle
                center={[country.countryInfo.lat, country.countryInfo.long]}
                color={casesTypeColors[casesType].hex}
                fillColor={casesTypeColors[casesType].hex}
                fillOpacity={0.4}
                radius={
                    Math.sqrt(country[casesType]) * casesTypeColors[casesType].multiplier
                }
            >

        <Popup>

            <div className='infoPopContainer'>
                <div className='infoFlag' style={{backgroundImage : `url(${country.countryInfo.flag})`}}>

                </div>
                <div className='infoCountryName'>

                    {country.country}

                </div>
                <div className='infoCases'></div>
                <div className='infoRecovered'></div>
                <div className='infoDeaths'></div>
            </div>


        </Popup>

            </Circle>
        ));
