import React, { Component , useState , useEffect} from 'react';
import '../css/forLogin.css'
import Axios from "axios";


const Login = () => {

    Axios.defaults.withCredentials = true;

    const[userMailLog ,  setUserMailLog] = useState();
    const[userPasswordLog ,  setUserPasswordLog] = useState();

      const loginFunc = () => {

        Axios.post('http://localhost:4000/login' , {mail:userMailLog , password:userPasswordLog})
            .then((response) => {

            if (response.data.message){

                console.log(response.data.message)
            }else{

                console.log(response.data[0].user_name)


            }
        })

      }


    return(
        <div>

            <div class="loginMs">

                <div class="divIs">
                    <h1 class="infs">Please Login to pass the CoronaVirus Test.</h1>
                </div>

                <div class="divHs">  <h3 class="infs">I don't have an Account</h3> </div>


                <a class="signUps">Register</a>

            </div>


            <div class="logins">

                <h1 class="loginT2s">Login</h1>


            </div>

            <div className="form__group6s fields">
                <input onChange = {(e) => setUserMailLog(e.target.value)} autoComplete="off" type="input" className="form__field" placeholder="Email" name="email"
                       id='emails' required/>
                <label htmlFor="emails" className="form__labels">Email</label>
            </div>


            <div className="form__group7s fields">
                <input onChange = {(e) => setUserPasswordLog(e.target.value)} autoComplete="off" type="password" className="form__fields" placeholder="Password"
                       name="passwords" id='passwords' required/>
                <label htmlFor="passwords" className="form__labels">Password</label>
            </div>



    <button onclick={loginFunc} class="signIns">Sign In</button>




        </div>
    );

}

export default Login;
